(106583, UNKNOWN, UNKNOWN, 'Azsuna will fall before the Legion!', 14, 0, 100, 53, 0, UNKNOWN, '109786', 'Arkethrax'),
(90390, UNKNOWN, UNKNOWN, 'Kneel, lowborn scum!', 12, 0, 100, UNKNOWN, 0, UNKNOWN, '93178', 'Tyndrissen to Player'),
(103162, UNKNOWN, UNKNOWN, 'Here''s a small taste of my fel magic.', 12, 0, 100, UNKNOWN, 0, 56984, '102125', 'Eredar Supplicant to Player'),
(89089, UNKNOWN, UNKNOWN, 'That voice...', 12, 0, 100, 1, 0, 60758, '93454', 'Prince Farondis to Player'),
(103176, UNKNOWN, UNKNOWN, 'You''re no match for me.', 12, 0, 100, UNKNOWN, 0, 55379, '102135', 'Mo''arg Crusher to Player'),
