(91185, UNKNOWN, UNKNOWN, 'Ah... my ''former'' master. Minion! Kill it!', 12, 0, 100, UNKNOWN, 0, UNKNOWN, '93327', 'Daglop to Player'),
(91419, UNKNOWN, UNKNOWN, 'Welcome to our humble place of business, traveler.', 12, 0, 100, 2, 0, 40019, '92192', 'Mr. Shackle to Player'),
(103363, UNKNOWN, UNKNOWN, 'So eager to be enslaved.', 12, 0, 100, UNKNOWN, 0, 55018, '97913', 'Soultorn Jailer to Player'),
