(106516, UNKNOWN, UNKNOWN, 'You should have joined us when you had the chance!', 12, 0, 100, UNKNOWN, 0, UNKNOWN, '115611', 'Empyrean Astrologer to Player'),
(89289, UNKNOWN, UNKNOWN, 'We were... going to Stormheim... after the Broken Shore.', 12, 1, 100, 5, 0, UNKNOWN, '92151', 'Queen''s Reprisal Sailor to Player'),
(89362, UNKNOWN, UNKNOWN, 'Archmage Khadgar? $n? An unexpected surprise.', 12, 0, 100, 6, 0, 52717, '121751', 'Kayn Sunfury to Player'),
(89673, UNKNOWN, UNKNOWN, 'Ah, visitors. It''s been a long time since Mardum, Allari.', 12, 0, 100, UNKNOWN, 0, UNKNOWN, '91449', 'Questioner Arev''naal to Player'),
(89661, UNKNOWN, UNKNOWN, 'Keep this up and I''ll have to give you extra homework.', 12, 0, 100, 1, 0, UNKNOWN, '111921', 'Instructor Nidriel to Player'),
(93488, UNKNOWN, UNKNOWN, 'Gentle Rosh likes you, my little $r. Now Gentle Rosh will leave.', 12, 0, 100, 11, 0, UNKNOWN, '95179', 'Gentle Rosh to Player'),
(91165, UNKNOWN, UNKNOWN, 'Make way for the little guy!', 12, 0, 100, UNKNOWN, 0, UNKNOWN, '93337', 'Daglop to Player'),
