(89975, UNKNOWN, UNKNOWN, 'Ahhhhh...', 12, 0, 100, UNKNOWN, 0, 52851, '93206', 'Senegos to Player'),
(91095, UNKNOWN, UNKNOWN, 'We are with you, champion. Let us end this beast!', 14, 0, 100, UNKNOWN, 0, UNKNOWN, '111760', 'Marius Felbane to Player'),
(91431, UNKNOWN, UNKNOWN, 'This place is not for you.', 12, 0, 100, UNKNOWN, 0, 52728, '93048', 'Spectral Student to Player'),
