(90623, UNKNOWN, UNKNOWN, 'You are nothing but a coward.', 12, 0, 100, UNKNOWN, 0, 57412, '93209', 'Stellagosa to Nightglaive the Traitor'),
(91131, UNKNOWN, UNKNOWN, 'This hunger is consuming my very mind. It is taking every ounce of my energy to just... just...', 12, 0, 100, UNKNOWN, 0, 62761, '92890', 'Runas the Shamed to Player'),
