(93487, UNKNOWN, UNKNOWN, 'You come open door. Gentle Rosh not crush you.', 12, 0, 100, 15, 0, UNKNOWN, '95200', 'Gentle Rosh to Player'),
(92445, UNKNOWN, UNKNOWN, 'You are easily dealt with.', 12, 0, 100, UNKNOWN, 0, 55205, '102131', 'Felguard Invader to Player'),
