(91402, UNKNOWN, UNKNOWN, 'Farondis! My most loyal subject. It''s been too long.', 12, 0, 100, 1, 0, 52897, '93453', 'Vision of Queen Azshara to Player'),
(91060, UNKNOWN, UNKNOWN, 'Get up, brother. Wake up.', 12, 0, 100, UNKNOWN, 0, 52866, '93207', 'Stellagosa to Player'),
(107962, UNKNOWN, UNKNOWN, 'As I fell, I saw one of the nightfallen slinking into our caves.', 12, 0, 100, 463, 0, UNKNOWN, '114497', 'Cedonu to Player'),
