(93495, UNKNOWN, UNKNOWN, 'I''ve been in there for days. Thank you, $c. I have got to go, if you know what I mean!', 12, 0, 100, 4, 0, UNKNOWN, '95184', 'Grace the Green to Player'),
(90621, UNKNOWN, UNKNOWN, 'Yes, my mistress...', 12, 0, 100, 2, 0, 52764, '93011', 'Nightglaive the Traitor to Player'),
(89134, UNKNOWN, UNKNOWN, 'Thank you, stranger.', 12, 0, 100, UNKNOWN, 0, UNKNOWN, '22945 - 53522 - 57476 - 77802 - 82465 - 83917 - 84593 - 91890 - 124192', 'Elder Aldryth to Player'),
