(89010, UNKNOWN, UNKNOWN, 'That stuff''s not for you.', 14, 0, 100, UNKNOWN, 0, UNKNOWN, '91750', 'Sleepy Dhow to Player'),
(89110, UNKNOWN, UNKNOWN, 'Freedom!', 12, 0, 100, 53, 0, UNKNOWN, '1691 - 46459 - 77656 - 91833', 'Shipwrecked Captive to Pile of Weapons'),
