(106990, UNKNOWN, UNKNOWN, 'What?! It''s mine. You can''t have it!', 12, 0, 100, UNKNOWN, 0, UNKNOWN, '111164', 'Chief Bitterbrine to Player'),
(88889, UNKNOWN, UNKNOWN, 'Come with me.', 12, 0, 100, UNKNOWN, 0, 52534, '91612 - 98995', 'Prince Farondis to Player'),
(91784, UNKNOWN, UNKNOWN, 'Guardians of the ritual, prepare yourselves! She will not be disappointed!', 14, 0, 100, UNKNOWN, 0, 54192, '101991', 'Warlord Parjesh to Hatecoil Warrior'),
