(89050, @GROUP_ID+1, @ID+, 'Come on. Let''s finish this!', 14, 0, 100, 15, 0, 0, 91769, 'Boss Whalebelly to Player'),
(89050, @GROUP_ID+2, @ID+, 'I told you I was going to chop you up into little chunks of chum, $p.', 12, 0, 100, 396, 0, 0, 91782, 'Boss Whalebelly to Bilgerudder'),
(89050, @GROUP_ID+3, @ID+, 'And that''s what I''m going to do!', 12, 0, 100, 0, 0, 0, 91783, 'Boss Whalebelly to Bilgerudder'),
(89050, @GROUP_ID+4, @ID+, 'WHAT? No...', 12, 0, 100, 0, 0, 0, 91787, 'Boss Whalebelly to Player'),
(89051, @GROUP_ID+0, @ID+, '$n, over here. Get me out of this cage.', 12, 0, 100, 1, 0, 0, 92015, 'Okuna Longtusk to Player'),
(89051, @GROUP_ID+1, @ID+, 'My hero! Say, you don''t happen to have my weapons?', 12, 0, 100, 1, 0, 0, 91843, 'Okuna Longtusk to Player'),
