(89089, @GROUP_ID+4, @ID+, 'KNEEL...', 12, 0, 100, 0, 0, 62551, 114144, 'Prince Farondis to Player'),
(89089, @GROUP_ID+5, @ID+, 'BEFORE YOU!!!', 12, 0, 100, 0, 0, 62552, 114145, 'Prince Farondis to Player'),
(89101, @GROUP_ID+0, @ID+, 'Go away, tiny creature, or I will have you for a snack.', 12, 0, 100, 0, 0, 13390, 92014, 'Prince Oceanus to Player'),
(89101, @GROUP_ID+1, @ID+, 'You want an audience? You''ve got one!', 12, 0, 100, 0, 0, 0, 91852, 'Prince Oceanus to Player'),
(89101, @GROUP_ID+2, @ID+, 'Forgive me, father.', 12, 0, 100, 0, 0, 0, 91447, 'Prince Oceanus to Player'),
(89104, @GROUP_ID+0, @ID+, 'Freedom!', 12, 0, 100, 53, 0, 700, PLEASE_SET_A_BROADCASTTEXT_ID, 'Shipwrecked Captive to Pile of Weapons'), -- BroadcastTextID: 1691 - 46459 - 77656 - 91833
(89104, @GROUP_ID+1, @ID+, 'Better to die free than as a slave.', 12, 0, 100, 53, 0, 700, 91838, 'Shipwrecked Captive to Pile of Weapons'),
(89104, @GROUP_ID+2, @ID+, 'Rise up. Let''s kill them all.', 12, 0, 100, 53, 0, 700, 91837, 'Shipwrecked Captive to Pile of Weapons'),
(89116, @GROUP_ID+0, @ID+, 'Make sure that prisoner doesn''t awaken, Parjesh.', 12, 0, 100, 1, 0, 53658, 91811, 'Athissa to Player'),
(89116, @GROUP_ID+1, @ID+, 'Be gone, spirit! The Tidestone belongs to us now!', 14, 0, 100, 1, 0, 53660, 91820, 'Athissa to Prince Farondis'),
(89116, @GROUP_ID+2, @ID+, 'Curse you and your kind, elf. The queen herself will hear of this!', 14, 0, 100, 1, 0, 53659, 91816, 'Athissa'),
(89117, @GROUP_ID+0, @ID+, 'Yes, my mistress.', 12, 0, 100, 1, 0, 52901, 91812, 'Warlord Parjesh to Player'),
(89117, @GROUP_ID+1, @ID+, 'You deal with our guest, Athissa. I shall take the prisoner to the slave pens.', 12, 0, 100, 1, 0, 52900, 91815, 'Warlord Parjesh to Prince Farondis'),
