INSERT INTO `quest_request_items` (`ID`, `EmoteOnComplete`, `EmoteOnIncomplete`, `EmoteOnCompleteDelay`, `EmoteOnIncompleteDelay`, `CompletionText`, `VerifiedBuild`) VALUES
(37959, 0, 0, 0, 0, 'Do you have them? Please tell me you have them, my friend.', 23420), -- The Hunger Returns
(37960, 0, 0, 0, 0, 'With each one of these cretins you slay, I grow stronger.', 23420), -- Leyline Abuse
(37859, 0, 0, 0, 0, 'Clever $r. Yes, a ley crystal should bring them back to their senses.', 23420), -- The Consumed
(38232, 0, 0, 0, 0, 'This land is ripe for the taking. Especially the olives. They are really ripe. And I love ripe olives.', 23420), -- Minion! Kill Them!
(37657, 6, 0, 0, 0, 'How goes the cleanup?', 23420), -- Making the World Safe for Profit
(37528, 0, 0, 0, 0, 'As soon as I can, I''ll arm the gladiators in Oceanus Cove.', 23420), -- Let Sleeping Giants Lie
(37469, 0, 0, 0, 0, '<The last remaining piece of the Tidestone lies before you.>', 23420), -- The Tidestone: Shattered
(37730, 0, 0, 0, 0, 'Were you able to meet with the headmistress?', 23420), -- The Headmistress' Keys
(37729, 0, 0, 0, 0, 'Go on, then! Get those feet moving!', 23420), -- Pop Quiz: Advanced Rune Drawing
(42371, 0, 0, 0, 0, 'I agree. Research can be grueling work.', 23420), -- Study Hall: Combat Research
(37736, 0, 0, 0, 0, 'Still trying to get into the academy, I see.', 23420), -- Dressing With Class
(42694, 0, 0, 0, 0, 'Thank you for your aid, $c.', 23420), -- Back from the Dead
(42693, 0, 0, 0, 0, 'I would have brought my own spell foci if SOMEONE hadn''t been rushing me...', 23420), -- You Never Know Until You Scry
(37728, 6, 0, 0, 0, 'Yes? You have the eyes?', 23420), -- Presentation is Everything
(37727, 6, 0, 0, 0, 'Are we speaking again because you have my eggs?', 23420), -- The Magister of Mixology
(37257, 0, 0, 0, 0, 'My people may not care much for me, but that will not stop me from caring for them.', 23420), -- Our Very Bones
(37256, 0, 0, 0, 0, '<The nightwatcher looks at you expectantly.>', 23420), -- They Came From the Sea
(42271, 0, 0, 0, 0, 'Oh no...', 23420), -- Their Dying Breaths
(37853, 0, 0, 0, 0, 'Call me a fool. I''d always just thought that grandfather would be with us forever.', 23420), -- The Death of the Eldest
(37450, 1, 0, 0, 0, 'If there''s a remnant of the Blue Dragonflight nearby, we should do whatever we can to ally ourselves with them.', 23420); -- Saving Stellagosa
